document.addEventListener("DOMContentLoaded",(function(){const e=document.querySelectorAll(".navbar-burger"),t=document.querySelectorAll(".navbar-menu");if(e.length&&t.length)for(let n=0;n<e.length;n++)e[n].addEventListener("click",(function(){for(let e=0;e<t.length;e++)t[e].classList.toggle("hidden")}));const n=document.querySelectorAll(".navbar-close"),l=document.querySelectorAll(".navbar-backdrop");if(n.length)for(let e=0;e<n.length;e++)n[e].addEventListener("click",(function(){for(let e=0;e<t.length;e++)t[e].classList.toggle("hidden")}));if(l.length)for(let e=0;e<l.length;e++)l[e].addEventListener("click",(function(){for(let e=0;e<t.length;e++)t[e].classList.toggle("hidden")}))}));
